function CareScale({ scaleValue, careType }) {
  const densite = [1, 2, 3, 4, 5, 6, 7, 8, 9];

  const scaleType = careType === "cute" ? "ðŸ˜€" : "ðŸ™„";

  return (
    <div>
      {densite.map((densiteElem) =>
        scaleValue >= densiteElem ? (
          <span key={densiteElem.toString()}>{scaleType}</span>
        ) : null
      )}
    </div>
  );
}

