            {catList.category === "femelle" ? <span>ðŸ‘§</span> : <span>ðŸ‘¦</span>}
            <CareScale careType="poils" scaleValue={catList.poils} />
            <CareScale careType="cute" scaleValue={catList.cute} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShoppingList;
