import { useState } from "react";
import "../styles/Cart.css";

function Cart() {
  const catPrice = 8;
  const [cart, updateCart] = useState(0);
  const [isOpen, setIsOpen] = useState(true);

  return isOpen ? (
    <div className="cart">
      <button className="cart-toogle-button" onClick={() => setIsOpen(false)}>
        Fermer
      </button>
      <h2>Panier</h2>
      <div>Chat : {catPrice}€</div>
      <button className="cart-add-button" onClick={() => updateCart(cart + 1)}>
        Ajouter
      </button>
      <p>Total : {catPrice * cart} €</p>
      <button onClick={() => updateCart(0)}>Vider le panier</button>
    </div>
  ) : (
    <div className="cart-closed">
      <button className="cart-toogle-button" onClick={() => setIsOpen(true)}>
        Ouvrir le panier
      </button>
    </div>
  );
}

export default Cart;
