import "../styles/Cart.css";
import { useState } from "react";

function Cart() {
  const catPrice = 8;
  const [cart, updateCart] = useState(0);
  const Freyja = 8;
  const Tyrael = 10;
  const pack = 15;

  return (
    <div className="cart">
      <h2>Panier</h2>
      Chat : {catPrice}€
      <ul>
        <li>Freyja : {Freyja} €</li>
        <li>Tyrael : {Tyrael} €</li>
        <li>pack : {pack} €</li>
      </ul>
      <button onClick={updateCart(catPrice + 1)}>Ajouter</button>
      <p>Total : {Freyja + Tyrael + pack} €</p>
    </div>
  );
}

export default Cart;
