import CareScale from "./CareScale";
import "../styles/CatItem.css";

function click(name) {
  alert(name);
}

function CatItem({ name, cover, id, poils, cute }) {
  return (
    <li key={id} className="cat-item" onClick={click(name)}>
      <img className="cat-item-cover" src={cover} alt={`${name} cover`} />
      {name}
      <div>
        <CareScale careType="poils" scaleValue={poils} />
        <CareScale careType="cute" scaleValue={cute} />
      </div>
    </li>
  );
}

export default CatItem;
